<?php// +----------------------------------------------------------------------// | User // +----------------------------------------------------------------------// | 版权所有 2015-2020 武汉市微易科技有限公司，并保留所有权利。// +----------------------------------------------------------------------// | 网站地址: https://www.kdfu.cn// +----------------------------------------------------------------------// | 这不是一个自由软件！您只能在不用于商业目的的前提下使用本程序// +----------------------------------------------------------------------// | 不允许对程序代码以任何形式任何目的的再发布// +----------------------------------------------------------------------// | Author: 微小易    Date: 2018-12-01// +----------------------------------------------------------------------namespace app\user\controller\item;use app\user\controller\Controller;use app\common\model\Category as CategoryModel;/** * 商品分类 * Class Category * @package app\user\controller\item */class Category extends Controller{	    /**     * 商品分类列表     * @return mixed     */    public function index()    {        $model = new CategoryModel;        $list = $model->getCacheTree();		        return $this->fetch('index', compact('list'));    }    /**     * 删除商品分类     * @param $category_id     * @return array     * @throws \think\exception\DbException     */    public function delete($category_id)    {        $model = CategoryModel::get($category_id);        if (!$model->remove($category_id)) {            return $this->renderError($model->getError() ?: '删除失败');        }        return $this->renderSuccess('删除成功');    }    /**     * 添加商品分类     * @return array|mixed     */    public function add()    {        $model = new CategoryModel;        if (!$this->request->isAjax()) {            // 获取所有地区            $list = $model->getCacheTree();            return $this->fetch('add', compact('list'));        }        // 新增记录        if ($model->add($this->postData('category'))) {            return $this->renderSuccess('添加成功', url('item.category/index'));        }        return $this->renderError($model->getError() ?: '添加失败');    }    /**     * 编辑商品分类     * @param $category_id     * @return array|mixed     * @throws \think\exception\DbException     */    public function edit($category_id)    {		        // 模板详情		        $model = CategoryModel::get($category_id, ['image']);		        if (!$this->request->isAjax()) {            // 获取所有地区            $list = $model->getCacheTree();		            return $this->fetch('edit', compact('model', 'list'));        }        // 更新记录        if ($model->edit($this->postData('category'))) {            return $this->renderSuccess('更新成功', url('item.category/index'));        }        return $this->renderError($model->getError() ?: '更新失败');    }		public function wxxcx(){				return $this->fetch();	}}